import NIO
import SwiftyJSON

/// An object which handles inbound I/O events for a `Channel`.
class TCPClientHandler: ChannelInboundHandler {
    // MARK: - Typealiases

    typealias InboundIn = ByteBuffer
    typealias OutboundOut = ByteBuffer

    // MARK: - Properties

    /// The message to send via a `Channel`.
    var message: Message

    // MARK: - Init Methods

    /// Creates an instance of `TCPClientHandler`.
    /// - Parameter message: The message to send via a `Channel`.
    init(message: Message) {
        self.message = message
    }

    // MARK: - Instance Methods

    func channelActive(context: ChannelHandlerContext) {
        var buffer = context.channel.allocator.buffer(capacity: message.rawValue.utf8.count)
        buffer.writeString(message.rawValue)
        context.writeAndFlush(wrapOutboundOut(buffer)).whenComplete { _ in
            context.close(promise: nil)
        }
    }

